const tabTriggers=document.querySelectorAll(".tab-trigger");Array.from(tabTriggers).forEach(t=>{const e=t.dataset.tab;t.addEventListener("click",function(){$.tab("change tab",e);const t=document.querySelector(".item[data-tab="+e+"]");t.closest(".menu").querySelector(".item.active").classList.remove("active"),t.classList.add("active")})});